    setCurrentSlide((prev) => (prev + 1) % slides.length);
  };

  return (
    <div>
      <AnimatePresence>
        {slides.map((slide, index) => (
          index === currentSlide && (
            <motion.div
              key={index}
              initial={{ opacity: 0 }}
              animate={{ opacity: 1 }}
              exit={{ opacity: 0 }}
            >
              <div className="slide">{slide}</div>
            </motion.div>
          )
        ))}
      </AnimatePresence>
      <button onClick={nextSlide}>Next Slide</button>
    </div>
  );
