    public static void main(String[] args) {
        Map<String, Integer> mutableMap = new HashMap<>();
        mutableMap.put("key1", 1);
        mutableMap.put("key2", 2);

        Map<String, Integer> immutableMap = Collections.unmodifiableMap(mutableMap);

